import User from"@/lib/model/userModel";import{NextResponse}from"next/server";import connectDB from"@/lib/db";import{jwtVerify}from"jose";export const POST=async req=>{console.log("fetching...");try{const body=await req.json(),{token:token}=body;if(!token)return NextResponse.json({message:"You are not logged in!"},{status:401});await connectDB();try{const secretKey=(new TextEncoder).encode(process.env.JWT_SECRET),{payload:payload}=await jwtVerify(token,secretKey),currentUser=await User.findById(payload.id);return currentUser?NextResponse.json(currentUser):NextResponse.json({message:"No user belongs to this token!"},{status:404})}catch(error){return NextResponse.json({message:"Invalid Token!"},{status:401})}}catch(error){return NextResponse.json({message:"Error fetching..",error:error.message},{status:500})}};